dunes-script-module.script:
// Get Global attributes from Configuration Element
var ConfigurationElement = System.getModule("com.neetcloud.cs.example").getConfigurationElementByName(configName, configPath);
System.log("attributeName: " + attributeName);
attributeName = attributeName.replace(/\s+/g, ' ');
return ConfigurationElement.getAttributeWithKey(attributeName).value
